package hw;

import java.util.Scanner;

public class hw01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scn=new Scanner(System.in);
		int n=scn.nextInt();
		for(int i=n;i>0;i--){
			for(int j=n;j>0;j--){
				System.out.print("*");
			}System.out.println();
		}
			
	}

}
